import React, { useState, useEffect } from "react";

// Mock banner data - you can replace with real data later
const banners = [
  {
    id: 1,
    image: "https://via.placeholder.com/1200x400/ff6b6b/ffffff?text=Special+Offer+Banner+1",
    title: "Special Movie Offers",
    description: "Get 50% off on all movie tickets"
  },
  {
    id: 2,
    image: "https://via.placeholder.com/1200x400/4ecdc4/ffffff?text=New+Releases+Banner+2",
    title: "New Releases",
    description: "Watch the latest blockbusters"
  },
  {
    id: 3,
    image: "https://via.placeholder.com/1200x400/45b7d1/ffffff?text=Weekend+Special+Banner+3",
    title: "Weekend Special",
    description: "Weekend movie marathon"
  }
];

function BannerSlider() {
  const [currentIndex, setCurrentIndex] = useState(0);

  // Auto-rotate banners every 5 seconds
  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentIndex((prevIndex) => 
        prevIndex === banners.length - 1 ? 0 : prevIndex + 1
      );
    }, 5000);

    return () => clearInterval(interval);
  }, []);

  const goToBanner = (index) => {
    setCurrentIndex(index);
  };

  return (
    <div style={{
      width: "100%",
      marginBottom: "2rem",
      borderRadius: "12px",
      overflow: "hidden",
      boxShadow: "0 4px 12px rgba(0,0,0,0.15)"
    }}>
      <div style={{
        position: "relative",
        width: "100%",
        height: "400px"
      }}>
        <img
          src={banners[currentIndex].image}
          alt={banners[currentIndex].title}
          style={{
            width: "100%",
            height: "100%",
            objectFit: "cover"
          }}
        />
        <div style={{
          position: "absolute",
          bottom: "0",
          left: "0",
          right: "0",
          background: "linear-gradient(transparent, rgba(0,0,0,0.7))",
          padding: "2rem",
          color: "white"
        }}>
          <h2 style={{ margin: "0 0 0.5rem 0", fontSize: "2rem" }}>
            {banners[currentIndex].title}
          </h2>
          <p style={{ margin: "0", fontSize: "1.1rem" }}>
            {banners[currentIndex].description}
          </p>
        </div>
      </div>
      
      {/* Banner indicators */}
      <div style={{
        display: "flex",
        justifyContent: "center",
        gap: "0.5rem",
        padding: "1rem"
      }}>
        {banners.map((banner, index) => (
          <div
            key={banner.id}
            onClick={() => goToBanner(index)}
            style={{
              width: "12px",
              height: "12px",
              borderRadius: "50%",
              backgroundColor: index === currentIndex ? "#ff6b6b" : "#ddd",
              cursor: "pointer",
              transition: "background-color 0.3s"
            }}
          />
        ))}
      </div>
    </div>
  );
}

export default BannerSlider;
