import React from "react";

const banners = [
  { img: "https://via.placeholder.com/800x250?text=Banner+1" },
  { img: "https://via.placeholder.com/800x250?text=Banner+2" },
  { img: "https://via.placeholder.com/800x250?text=Banner+3" }
];

function BannerSlider() {
  // For simplicity, just show the first banner
  return (
    <div style={{
      width: "100%",
      overflow: "hidden",
      margin: "2rem 0"
    }}>
      <img src={banners[0].img} alt="Banner" style={{width: "100%", borderRadius: "12px"}} />
    </div>
  );
}

export default BannerSlider;
