import React, { useState } from "react";
import MovieList from "./components/MovieList";
import MovieDetail from "./components/MovieDetail";

const API_KEY = "YOUR_OMDB_API_KEY"; // <-- Replace with your OMDb API key

function App() {
  const [query, setQuery] = useState("");
  const [movies, setMovies] = useState([]);
  const [selectedMovie, setSelectedMovie] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  const searchMovies = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError("");
    setSelectedMovie(null);
    try {
      const res = await fetch(
        `https://www.omdbapi.com/?apikey=${API_KEY}&s=${encodeURIComponent(query)}`
      );
      const data = await res.json();
      if (data.Response === "True") {
        setMovies(data.Search);
      } else {
        setMovies([]);
        setError(data.Error);
      }
    } catch (err) {
      setError("Failed to fetch movies.");
    }
    setLoading(false);
  };

  const fetchMovieDetail = async (imdbID) => {
    setLoading(true);
    setError("");
    try {
      const res = await fetch(
        `https://www.omdbapi.com/?apikey=${API_KEY}&i=${imdbID}&plot=full`
      );
      const data = await res.json();
      if (data.Response === "True") {
        setSelectedMovie(data);
      } else {
        setError(data.Error);
      }
    } catch (err) {
      setError("Failed to fetch movie details.");
    }
    setLoading(false);
  };

  return (
    <div className="container">
      <h1>Movie Search App</h1>
      <form onSubmit={searchMovies} className="search-form">
        <input
          type="text"
          placeholder="Search for a movie..."
          value={query}
          onChange={(e) => setQuery(e.target.value)}
          required
        />
        <button type="submit" disabled={loading}>
          {loading ? "Searching..." : "Search"}
        </button>
      </form>
      {error && <div className="error">{error}</div>}
      {!selectedMovie ? (
        <MovieList movies={movies} onMovieClick={fetchMovieDetail} />
      ) : (
        <MovieDetail movie={selectedMovie} onBack={() => setSelectedMovie(null)} />
      )}
    </div>
  );
}

export default App;
