import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { getMovieDetail } from "../api/omdb";
import BookingForm from "../components/BookingForm";

function BookingPage() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [movie, setMovie] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [booked, setBooked] = useState(false);

  useEffect(() => {
    const fetchMovie = async () => {
      try {
        setLoading(true);
        const data = await getMovieDetail(id);
        if (data.Response === "True") {
          setMovie(data);
        } else {
          setError(data.Error || "Movie not found");
        }
      } catch (error) {
        setError("Failed to fetch movie details");
      } finally {
        setLoading(false);
      }
    };

    fetchMovie();
  }, [id]);

  const handleBook = (data) => {
    console.log("Booking data:", data);
    setBooked(true);
  };

  if (loading) {
    return (
      <div style={{
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        height: "50vh"
      }}>
        <div style={{
          display: "inline-block",
          width: "50px",
          height: "50px",
          border: "3px solid #f3f3f3",
          borderTop: "3px solid #ff9800",
          borderRadius: "50%",
          animation: "spin 1s linear infinite"
        }}></div>
        <p style={{marginLeft: "1rem"}}>Loading movie details...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div style={{
        textAlign: "center",
        padding: "2rem",
        maxWidth: "600px",
        margin: "0 auto"
      }}>
        <h2>Error</h2>
        <p>{error}</p>
        <button
          onClick={() => navigate("/")}
          style={{
            background: "#ff9800",
            color: "#fff",
            padding: "0.7rem 2rem",
            borderRadius: "6px",
            border: "none",
            fontSize: "1.1rem",
            cursor: "pointer",
            marginTop: "1rem"
          }}
        >
          Back to Home
        </button>
      </div>
    );
  }

  if (booked) {
    return (
      <div style={{
        textAlign: "center",
        padding: "2rem",
        maxWidth: "600px",
        margin: "0 auto"
      }}>
        <h2>Booking Confirmed!</h2>
        <p>Thank you for your booking. You will receive a confirmation email shortly.</p>
        <button
          onClick={() => navigate("/")}
          style={{
            background: "#ff9800",
            color: "#fff",
            padding: "0.7rem 2rem",
            borderRadius: "6px",
            border: "none",
            fontSize: "1.1rem",
            cursor: "pointer",
            marginTop: "1rem"
          }}
        >
          Back to Home
        </button>
      </div>
    );
  }

  return (
    <div style={{
      maxWidth: "1200px",
      margin: "0 auto",
      padding: "2rem"
    }}>
      <BookingForm movie={movie} onBook={handleBook} />
    </div>
  );
}

export default BookingPage;
