import React, { useState } from "react";

function BookingForm({ movie, onBook }) {
  const [name, setName] = useState("");
  const [seats, setSeats] = useState(1);

  const handleSubmit = (e) => {
    e.preventDefault();
    onBook({ name, seats });
  };

  return (
    <form onSubmit={handleSubmit} style={{maxWidth: "400px", margin: "2rem auto"}}>
      <h2>Book Tickets for {movie.title}</h2>
      <div style={{marginBottom: "1rem"}}>
        <label>Name:</label>
        <input
          type="text"
          value={name}
          required
          onChange={e => setName(e.target.value)}
          style={{width: "100%", padding: "0.5rem", borderRadius: "4px", border: "none", marginTop: "0.5rem"}}
        />
      </div>
      <div style={{marginBottom: "1rem"}}>
        <label>Seats:</label>
        <input
          type="number"
          min="1"
          max="10"
          value={seats}
          required
          onChange={e => setSeats(e.target.value)}
          style={{width: "100%", padding: "0.5rem", borderRadius: "4px", border: "none", marginTop: "0.5rem"}}
        />
      </div>
      <button type="submit" style={{
        padding: "0.7rem 2rem",
        background: "#ff9800",
        color: "#fff",
        border: "none",
        borderRadius: "6px",
        fontSize: "1.1rem",
        cursor: "pointer"
      }}>Book</button>
    </form>
  );
}

export default BookingForm;
