import React, { useState } from "react";
import { useParams } from "react-router-dom";
import BookingForm from "../components/BookingForm";

// Mock movie data (should match Home.jsx)
const movies = [
  {
    id: "1",
    title: "The Avengers",
    year: "2012",
    poster: "https://m.media-amazon.com/images/I/71niXI3lxlL._AC_SY679_.jpg"
  },
  {
    id: "2",
    title: "Avengers: Endgame",
    year: "2019",
    poster: "https://m.media-amazon.com/images/I/81ExhpBEbHL._AC_SY679_.jpg"
  },
  {
    id: "3",
    title: "Inception",
    year: "2010",
    poster: "https://m.media-amazon.com/images/I/51s+Qp1pQSL._AC_.jpg"
  }
];

function BookingPage() {
  const { id } = useParams();
  const movie = movies.find(m => m.id === id);
  const [booked, setBooked] = useState(false);

  const handleBook = (data) => {
    setBooked(true);
  };

  return (
    <div className="container">
      {booked ? (
        <div style={{textAlign: "center", marginTop: "2rem"}}>
          <h2>Booking Confirmed!</h2>
          <p>Enjoy your movie: <strong>{movie.title}</strong></p>
        </div>
      ) : (
        <BookingForm movie={movie} onBook={handleBook} />
      )}
    </div>
  );
}

export default BookingPage;
