import React from "react";
import { useParams } from "react-router-dom";
import MovieDetail from "../components/MovieDetail";

// Mock movie data (should match Home.jsx)
const movies = [
  {
    id: "1",
    title: "The Avengers",
    year: "2012",
    poster: "https://m.media-amazon.com/images/I/71niXI3lxlL._AC_SY679_.jpg",
    genre: "Action, Sci-Fi",
    director: "Joss Whedon",
    cast: "Robert Downey Jr., Chris Evans, Scarlett Johansson",
    plot: "Earth's mightiest heroes must come together and learn to fight as a team."
  },
  {
    id: "2",
    title: "Avengers: Endgame",
    year: "2019",
    poster: "https://m.media-amazon.com/images/I/81ExhpBEbHL._AC_SY679_.jpg",
    genre: "Action, Adventure, Drama",
    director: "Anthony Russo, Joe Russo",
    cast: "Robert Downey Jr., Chris Evans, Mark Ruffalo",
    plot: "After the devastating events of Infinity War, the Avengers assemble once more."
  },
  {
    id: "3",
    title: "Inception",
    year: "2010",
    poster: "https://m.media-amazon.com/images/I/51s+Qp1pQSL._AC_.jpg",
    genre: "Action, Adventure, Sci-Fi",
    director: "Christopher Nolan",
    cast: "Leonardo DiCaprio, Joseph Gordon-Levitt, Ellen Page",
    plot: "A thief who steals corporate secrets through dream-sharing technology."
  }
];

function MoviePage() {
  const { id } = useParams();
  const movie = movies.find(m => m.id === id);

  return (
    <div className="container">
      <MovieDetail movie={movie} />
    </div>
  );
}

export default MoviePage;
