import React, { useState } from "react";

function BookingForm({ movie, onBook }) {
  const [name, setName] = useState("");
  const [seats, setSeats] = useState(1);

  // Handle case when movie is undefined
  if (!movie) {
    return (
      <div style={{
        textAlign: "center",
        padding: "2rem",
        color: "#bdbdbd"
      }}>
        <h2>Movie Not Found</h2>
        <p>Sorry, the movie details could not be loaded.</p>
      </div>
    );
  }

  const handleSubmit = (e) => {
    e.preventDefault();
    onBook({ name, seats: parseInt(seats) });
  };

  const handleSeatsChange = (e) => {
    const value = parseInt(e.target.value);
    if (value >= 1 && value <= 10) {
      setSeats(value);
    }
  };

  return (
    <form onSubmit={handleSubmit} style={{
      maxWidth: "400px",
      margin: "2rem auto",
      background: "#292929",
      padding: "2rem",
      borderRadius: "12px"
    }}>
      <h2>Book Tickets for {movie.title || movie.Title || "Movie"}</h2>
      <div style={{marginBottom: "1rem"}}>
        <label style={{display: "block", marginBottom: "0.5rem"}}>Name:</label>
        <input
          type="text"
          value={name}
          required
          onChange={e => setName(e.target.value)}
          style={{
            width: "100%",
            padding: "0.7rem",
            borderRadius: "4px",
            border: "1px solid #444",
            background: "#181818",
            color: "#fff"
          }}
        />
      </div>
      
      <div style={{marginBottom: "1rem"}}>
        <label style={{display: "block", marginBottom: "0.5rem"}}>Number of Seats:</label>
        <input
          type="number"
          min="1"
          max="10"
          value={seats}
          required
          onChange={handleSeatsChange}
          style={{
            width: "100%",
            padding: "0.7rem",
            borderRadius: "4px",
            border: "1px solid #444",
            background: "#181818",
            color: "#fff"
          }}
        />
        <small style={{color: "#bdbdbd", fontSize: "0.8rem"}}>
          Select between 1-10 seats
        </small>
      </div>
      
      <button type="submit" style={{
        width: "100%",
        padding: "1rem",
        background: "#ff9800",
        color: "#fff",
        border: "none",
        borderRadius: "6px",
        fontSize: "1.1rem",
        cursor: "pointer"
      }}>
        Book {seats} Ticket{seats > 1 ? 's' : ''}
      </button>
    </form>
  );
}

export default BookingForm;
