import React from "react";
import MovieCard from "./MovieCard";

function MovieCarousel({ movies }) {
  return (
    <div style={{
      display: "flex",
      overflowX: "auto",
      gap: "1.5rem",
      padding: "1rem 2rem"
    }}>
      {movies.map(movie => (
        <MovieCard key={movie.imdbID} movie={movie} />
      ))}
    </div>
  );
}

export default MovieCarousel;
